import React, { useEffect, useState } from "react";
import "./Profile.css";

const Profile = ({ username }) => {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showPosts, setShowPosts] = useState(false);
  const [showActivities, setShowActivities] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/user/profile/${username}`)
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch user profile");
        return res.json();
      })
      .then((data) => {
        setProfileData(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [username]);

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div>Error: {error}</div>;

  const { user, posts, activities } = profileData;

  return (
    <div className="profile-container">
      <h2>{user.username}'s Profile</h2>
      <div className="profile-info">
        <p>Followers: {user.followers.length}</p>
        <p>Following: {user.following.length}</p>
      </div>

      <div className="button-group">
        <button
          className={`toggle-btn ${showPosts ? "active" : ""}`}
          onClick={() => {
            setShowPosts(!showPosts);
            if (showActivities) setShowActivities(false);
          }}
        >
          Posts
        </button>
        <button
          className={`toggle-btn ${showActivities ? "active" : ""}`}
          onClick={() => {
            setShowActivities(!showActivities);
            if (showPosts) setShowPosts(false);
          }}
        >
          Activities
        </button>
      </div>

      {showPosts && (
        <>
          <h3 className="section-title">Posts</h3>
          {posts.length === 0 ? (
            <p>No posts yet.</p>
          ) : (
            <ul className="post-list">
              {posts.map((post) => (
                <li key={post._id}>
                  {post.content} <br />
                  <small>{new Date(post.createdAt).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {showActivities && (
        <>
          <h3 className="section-title">Activities</h3>
          {activities.length === 0 ? (
            <p>No activities yet.</p>
          ) : (
            <ul className="activity-list">
              {activities.map((act) => (
                <li key={act._id}>
                  {act.activity} <br />
                  <small>{new Date(act.date).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default Profile;
