import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // <-- CHANGED: import useNavigate

const LoginForm = () => {
  const [user, setUser] = useState({ username: '', password: '' });
  const navigate = useNavigate(); // <-- CHANGED: initialize navigate

  const onChange = e => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('/user/login', { // <-- CHANGED: login request
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user),
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem('username', user.username); // <-- CHANGED: store username
        navigate(`/profile/${user.username}`); // <-- CHANGED: redirect to profile
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input
        name="username"
        value={user.username}
        onChange={onChange}
        placeholder="Username"
        required
      />
      <input
        type="password"
        name="password"
        value={user.password}
        onChange={onChange}
        placeholder="Password"
        required
      />
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginForm;
