import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Profile = () => {
  const { username } = useParams(); // get username from URL
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await fetch(`http://localhost:5000/user/profile/${username}`);
        if (!res.ok) {
          throw new Error("Failed to fetch user profile");
        }
        const data = await res.json();
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [username]);

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!user) return <p>User not found.</p>;

  return (
    <div className="profile-container">
      <h2>Profile of {user.username}</h2>
      <p><strong>Followers:</strong> {user.followers.length > 0 ? user.followers.join(", ") : "No followers"}</p>
      <p><strong>Following:</strong> {user.following.length > 0 ? user.following.join(", ") : "Not following anyone"}</p>
    </div>
  );
};

export default Profile;
