import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Profile = () => {
  const { username } = useParams();

  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetch(`/user/profile/${username}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch user profile");
        return res.json();
      })
      .then(data => {
        setProfileData(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [username]);

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div>Error: {error}</div>;

  const { user, posts, activities } = profileData;

  return (
    <div>
      <h2>{user.username}'s Profile</h2>
      <p>Followers: {user.followers.length}</p>
      <p>Following: {user.following.length}</p>

      <h3>Posts</h3>
      {posts.length === 0 ? <p>No posts yet.</p> : (
        <ul>
          {posts.map(post => (
            <li key={post._id}>{post.content} <br /><small>{new Date(post.createdAt).toLocaleString()}</small></li>
          ))}
        </ul>
      )}

      <h3>Activities</h3>
      {activities.length === 0 ? <p>No activities yet.</p> : (
        <ul>
          {activities.map(act => (
            <li key={act._id}>{act.comment} <br /><small>{new Date(act.createdAt || act.date).toLocaleString()}</small></li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
