import React, { useEffect, useState } from "react";
import "./Profile.css";
import { useParams, useNavigate } from "react-router-dom";

const Profile = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // New states for showing add forms
  const [showPostForm, setShowPostForm] = useState(false);
  const [showActivityForm, setShowActivityForm] = useState(false);

  // New states for input fields
  const [newPostContent, setNewPostContent] = useState("");
  const [newActivityComment, setNewActivityComment] = useState("");

  useEffect(() => {
    fetch(`/user/profile/${username}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch user profile");
        return res.json();
      })
      .then(data => {
        setProfileData(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [username]);

  const handleLogout = () => {
    navigate("/");
  };

  // Add Post handler
  const handleAddPost = async (e) => {
    e.preventDefault();
    if (!newPostContent.trim()) return alert("Post content can't be empty");

    try {
      const res = await fetch("/post/upload", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, content: newPostContent }),
      });
      if (!res.ok) throw new Error("Failed to add post");
      const post = await res.json();

      setProfileData((prev) => ({
        ...prev,
        posts: [post, ...prev.posts],
      }));
      setNewPostContent("");
      setShowPostForm(false);
    } catch (err) {
      alert(err.message);
    }
  };

  // Add Activity handler
  const handleAddActivity = async (e) => {
    e.preventDefault();
    if (!newActivityComment.trim()) return alert("Activity comment can't be empty");

    try {
      const res = await fetch("/activity/createComment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, comment: newActivityComment }),
      });
      if (!res.ok) throw new Error("Failed to add activity");
      const activity = await res.json();

      setProfileData((prev) => ({
        ...prev,
        activities: [activity, ...prev.activities],
      }));
      setNewActivityComment("");
      setShowActivityForm(false);
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div>Error: {error}</div>;

  const { user, posts, activities } = profileData;

  return (
    <div className="profile-container">
      <button 
        className="btn btn-danger logout-button" 
        onClick={handleLogout}
        style={{ float: 'right', marginBottom: '20px' }}
      >
        Logout
      </button>

      <h2>Welcome, {user.username}!!!</h2>
      <div className="profile-info">
        <p>Followers: {user.followers.length}</p>
        <p>Following: {user.following.length}</p>
      </div>

      {/* Buttons to toggle forms */}
      <div style={{ marginBottom: "20px" }}>
        <button
          className="btn btn-primary me-3"
          onClick={() => {
            setShowPostForm(!showPostForm);
            if (showActivityForm) setShowActivityForm(false);
          }}
        >
          {showPostForm ? "Cancel Post" : "Add Post"}
        </button>
        <button
          className="btn btn-secondary"
          onClick={() => {
            setShowActivityForm(!showActivityForm);
            if (showPostForm) setShowPostForm(false);
          }}
        >
          {showActivityForm ? "Cancel Activity" : "Add Activity"}
        </button>
      </div>

      {/* Post form */}
      {showPostForm && (
        <form onSubmit={handleAddPost} className="mb-4">
          <textarea
            className="form-control mb-2"
            placeholder="Write your post here..."
            value={newPostContent}
            onChange={(e) => setNewPostContent(e.target.value)}
            rows={3}
            required
          />
          <button type="submit" className="btn btn-success">Submit Post</button>
        </form>
      )}

      {/* Activity form */}
      {showActivityForm && (
        <form onSubmit={handleAddActivity} className="mb-4">
          <textarea
            className="form-control mb-2"
            placeholder="Write your activity comment here..."
            value={newActivityComment}
            onChange={(e) => setNewActivityComment(e.target.value)}
            rows={3}
            required
          />
          <button type="submit" className="btn btn-success">Submit Activity</button>
        </form>
      )}

      <h3 className="section-title">Posts</h3>
      {posts.length === 0 ? <p>No posts yet.</p> : (
        <ul className="post-list">
          {posts.map(post => (
            <li key={post._id}>
              {post.content}
              <small>{new Date(post.createdAt).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}

      <h3 className="section-title">Activities</h3>
      {activities.length === 0 ? <p>No activities yet.</p> : (
        <ul className="activity-list">
          {activities.map(act => (
            <li key={act._id}>
              {act.comment}
              <small>{new Date(act.createdAt || act.date).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
