import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";  // <-- import useNavigate

const Profile = ({ username }) => {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();  // <-- initialize navigate

  useEffect(() => {
    fetch(`http://localhost:5000/user/profile/${username}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch user profile");
        return res.json();
      })
      .then(data => {
        setProfileData(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [username]);

  const handleLogout = () => {
    // You can also clear auth tokens or localStorage here if needed
    navigate('/');  // redirect to home page
  };

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div>Error: {error}</div>;

  const { user, posts, activities } = profileData;

  return (
    <div className="profile-container">
      <button 
        className="btn btn-danger logout-button" 
        onClick={handleLogout}
        style={{ float: 'right', marginBottom: '20px' }}
      >
        Logout
      </button>

      <h2>{user.username}'s Profile</h2>
      <p>Followers: {user.followers.length}</p>
      <p>Following: {user.following.length}</p>

      <h3>Posts</h3>
      {posts.length === 0 ? <p>No posts yet.</p> : (
        <ul className="post-list">
          {posts.map(post => (
            <li key={post._id}>{post.content} <br /><small>{new Date(post.createdAt).toLocaleString()}</small></li>
          ))}
        </ul>
      )}

      <h3>Activities</h3>
      {activities.length === 0 ? <p>No activities yet.</p> : (
        <ul className="activity-list">
          {activities.map(act => (
            <li key={act._id}>{act.activity} <br /><small>{new Date(act.date).toLocaleString()}</small></li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
