import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './Form.css';
import { useNavigate } from "react-router-dom";  // <-- for redirect

const LoginForm = () => {
  const navigate = useNavigate();  // <-- initialize navigate

  const [user, setUser] = useState({
    email: "",      // changed to email because your form uses email field, if username use username
    password: ""
  });

  // Handle input changes
  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  // Handle form submit
  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/user/login", {  // Adjust backend URL if needed
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      const data = await res.json();

      if (res.ok) {
        // Save username or email in localStorage for Profile fetch
        localStorage.setItem("username", user.email);  // or user.username if you have username field

        // Redirect to profile page with username/email
        navigate(`/profile/${user.email}`);
      } else {
        alert(data.message || "Login failed");
      }
    } catch (error) {
      console.error(error);
      alert("An error occurred while logging in");
    }
  };

  return (
    <div className="form-container">
      <h2>Login</h2>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            name="email"       // <-- Added name for controlled input
            value={user.email}
            onChange={onChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Password:</label>
          <input
            type="password"
            className="form-control"
            placeholder="Enter password"
            name="password"    // <-- Added name for controlled input
            value={user.password}
            onChange={onChange}
            required
          />
        </div>
        <button className="btn btn-success">Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
